import { React, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Register = () => {
  const baseUrl = "http://localhost:8001"

  const [userList,setUserList] = useState([]);
  const [user_name, setname] = useState("");
  const [user_account,setaccout] = useState("");
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const onUserHandler = (event) => {
    setname(event.currentTarget.value);
  };

  const onAccountHandler = (event) => {
    setaccout(event.currentTarget.value);
  }

  const onIdHandler = (event) => {
    setId(event.currentTarget.value);
    console.log(event.currentTarget.value);
  };

  const onPasswordHandler = (event) => {
    setPassword(event.currentTarget.value);
  };

  const onConfirmPasswordHandler = (event) => {
    setConfirmPassword(event.currentTarget.value);
  };

  const CreateUser = async () => {
    await axios.post(baseUrl+'/test',{
      user_name : user_name,
      user_email : id,
      user_password : password,
      user_account : user_account
    })
      .then((response) =>{
        console.log(response.data);
        setname("");
        setId("");
        setaccout("");
        setPassword("");
        setConfirmPassword("");
      })
      .catch((error) => {
        console.log(error);
        console.log(error.response.status);
        console.log(error.response.headers);
      })
  }

  const onSubmit = (event) => {
    if (localStorage.getItem(id) !== null) {
      event.preventDefault();
      alert("Given ID already exists");
    } else if (password !== confirmPassword) {
      event.preventDefault();
      alert("Passwords do not match");
    } else if (password.length < 5) {
      event.preventDefault();
      alert("Password is too short");
    } else {
      alert("Succesfully Registered");
      console.log(user_name,user_account,id,password);
      CreateUser();
    }
  };

  return (
    <div>
      <h1
        style={{ margin: "10px auto", display: "block", textAlign: "center" }}
      >
        Register
      </h1>
      <form style={{ margin: "10px auto", textAlign: "center" }}>
        <div>
          <input
            placeholder="name"
            value={user_name}
            onChange={onUserHandler}
          />
        </div>
        <div>
          <input
            placeholder="account"
            value={user_account}
            onChange={onAccountHandler}
          />
        </div>
        <div>
          <input placeholder="id" value={id} onChange={onIdHandler} />
        </div>
        <div>
          <input tyep = "password"
            placeholder="password"
            value={password}
            onChange={onPasswordHandler}
          />
        </div>
        <div>
          <input type = "password"
            placeholder="confirm password"
            value={confirmPassword}
            onChange={onConfirmPasswordHandler}
          />
        </div>
          <div>
            <button onClick={onSubmit}>Submit</button>
          </div>
      </form>
    </div>
  );
};

export default Register;
